INSERT INTO "LESSON" ("ENTITY_ID", "CREATION_DATE", "TITLE", "CONTENT", "LEVEL_ID") VALUES (1000048, TO_DATE('20170520215838', 'YYYYMMDDHH24MISS'), 'Verb To Be', '<ul><li><strong>When we talk about location</strong>: in California, from Brazil, at home, in the park, etc.</li><li><strong>When we talk about professions</strong>: she is a teacher. He is a doctor. They are police officers.</li><li><strong>Nationalities</strong>: German, Russian, American, etc.</li><li><strong>When we talk about the age</strong>: I am twenty years old. My wife is 39 years old.</li><li><strong>To describe marital status</strong>: single, married, divorced, widow, widower.</li><li>Finally we use to be with adjectives to <strong>describe physical or emotional states</strong>: to be hungry, angry,</li><li>upset, happy, sad, afraid, etc.</li></ul>', 1);
